// Trong SizeBar.js
import React, { useState } from 'react';
import './SizeBar.css';
import chev from '../../assets/icon/left-chevron.svg';
import ListSize from '../NavBar/ListSize';

const SizeBar = ({ children }) => {
  const [isOpen, setIsOpen] = useState(true);

  const toggleSizeBar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`SizeBar ${isOpen ? 'open' : 'closed'}`}>
      <button className="toggleButton" onClick={toggleSizeBar}>
        <img src={chev} alt="ChevLogo"/> 
        {isOpen ? 'Close' : 'Open'}
      </button>
      {isOpen && <ListSize />}
      {children}
    </div>
  );
};

export default SizeBar;
