import React, { useState } from 'react';
import './SizeBar.css';
import chev from '../../assets/icon/left-chevron.svg';
import ListSize from '../NavBar/ListSize';
import TabButton from '../TabButton';

const SizeBar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSizeBar = () => {
    setIsOpen(!isOpen);
  };

  function handleSelect(selectedButton) {
    //selectedButton => 'component'
    console.log('hello world!');
  }

  return (
    <div className={`SizeBar ${isOpen? cssOpen : cssClose}`}>
      <button className="toggleButton" onClick={toggleSizeBar}>
        <img src={chev} alt="ChevLogo" />
      </button>
      
      <div className="menu">
        {isOpen && (
          <section id="examples">
            <menu>
              <TabButton onSelect={() => handleSelect('Home')} isOpen={isOpen}>
                Home
              </TabButton>
              <TabButton onSelect={() => handleSelect('Quản lý Office')} isOpen={isOpen}>
                Quản lý Office
              </TabButton>
              <TabButton onSelect={() => handleSelect('Quản lý tuyến xe')} isOpen={isOpen}>
                Quản lý tuyến xe
              </TabButton>
              <TabButton onSelect={() => handleSelect('Quản lý đơn hàng')} isOpen={isOpen}>
                Quản lý đơn hàng
              </TabButton>
              <TabButton onSelect={() => handleSelect('Quản lý nhân viên')} isOpen={isOpen}>
                Quản lý nhân viên
              </TabButton>
              <TabButton onSelect={() => handleSelect('Cài đặt')} isOpen={isOpen}>
                Cài đặt
              </TabButton>
            </menu>
          </section>
        )}
      </div>
    </div>
  );
};

export default SizeBar;
